import React, { useState, useRef, useEffect } from 'react';
import styled from 'styled-components';
import { useBoard } from '../hooks/useBoard';

const SelectorContainer = styled.div`
  position: relative;
  display: flex;
  align-items: center;
  gap: 10px;
  z-index: 1000;

  /* Ensure the container creates a new stacking context */
  &:focus-within {
    z-index: 10000;
  }
`;

const SelectorButton = styled.button`
  background: linear-gradient(135deg, #3498db, #2980b9);
  color: white;
  border: none;
  border-radius: 12px;
  padding: 12px 20px;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 10px;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  min-width: 160px;
  justify-content: space-between;
  box-shadow: 0 4px 15px rgba(52, 152, 219, 0.3);
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
    transition: left 0.5s;
  }

  &:hover {
    background: linear-gradient(135deg, #2980b9, #1e6ca3);
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(52, 152, 219, 0.4);

    &::before {
      left: 100%;
    }
  }

  &:active {
    transform: scale(0.95);
  }
`;

const CreateButton = styled.button`
  background: linear-gradient(135deg, #f4d03f, #f7dc6f);
  color: #2c3e50;
  border: none;
  border-radius: 12px;
  padding: 12px 20px;
  font-size: 14px;
  font-weight: 700;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 10px;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  box-shadow: 0 4px 15px rgba(244, 208, 63, 0.3);
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.3), transparent);
    transition: left 0.5s;
  }

  &:hover {
    background: linear-gradient(135deg, #f7dc6f, #f9e79f);
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(244, 208, 63, 0.4);

    &::before {
      left: 100%;
    }
  }

  &:active {
    transform: scale(0.95);
  }
`;

const Dropdown = styled.div`
  position: absolute;
  top: 100%;
  right: 0;
  background: rgba(255, 255, 255, 0.98);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(52, 152, 219, 0.3);
  border-radius: 16px;
  box-shadow: 
    0 20px 60px rgba(30, 60, 114, 0.25),
    0 8px 24px rgba(52, 152, 219, 0.15);
  z-index: 9999;
  min-width: 380px;
  max-width: 480px;
  max-height: 500px;
  overflow-y: auto;
  margin-top: 8px;
  animation: slideInDown 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  transform-origin: top right;

  @keyframes slideInDown {
    from {
      opacity: 0;
      transform: translateY(-15px) scale(0.92);
      filter: blur(4px);
    }
    to {
      opacity: 1;
      transform: translateY(0) scale(1);
      filter: blur(0);
    }
  }

  &::-webkit-scrollbar {
    width: 6px;
  }

  &::-webkit-scrollbar-track {
    background: rgba(244, 208, 63, 0.1);
    border-radius: 3px;
  }

  &::-webkit-scrollbar-thumb {
    background: linear-gradient(135deg, #f4d03f, #f7dc6f);
    border-radius: 3px;

    &:hover {
      background: linear-gradient(135deg, #f39c12, #f4d03f);
    }
  }

  /* Ensure dropdown is on top of everything */
  &::before {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    z-index: -1;
    pointer-events: none;
  }
`;

const DropdownSection = styled.div`
  padding: 20px;
  border-bottom: 1px solid rgba(52, 152, 219, 0.1);
  animation: fadeIn 0.6s ease-out;

  &:last-child {
    border-bottom: none;
  }

  @keyframes fadeIn {
    from { opacity: 0; }
    to { opacity: 1; }
  }
`;

