import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const StatusContainer = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 8px 12px;
  border-radius: 6px;
  background: ${props => props.connected ? 'rgba(46, 204, 113, 0.1)' : 'rgba(231, 76, 60, 0.1)'};
  border: 1px solid ${props => props.connected ? '#2ecc71' : '#e74c3c'};
  font-size: 12px;
  font-weight: 500;
  color: ${props => props.connected ? '#27ae60' : '#c0392b'};
  min-width: 120px;
  justify-content: center;
`;

const StatusIndicator = styled.div`
  width: 8px;
  height: 8px;
  border-radius: 50%;
  background: ${props => props.connected ? '#2ecc71' : '#e74c3c'};
  animation: ${props => props.connected ? 'pulse-success' : 'pulse-error'} 2s infinite;

  @keyframes pulse-success {
    0% { opacity: 1; }
    50% { opacity: 0.6; }
    100% { opacity: 1; }
  }

  @keyframes pulse-error {
    0% { opacity: 1; transform: scale(1); }
    50% { opacity: 0.7; transform: scale(0.9); }
    100% { opacity: 1; transform: scale(1); }
  }
`;

const StatusText = styled.span`
  font-weight: 600;
`;

const SocketId = styled.div`
  font-size: 10px;
  color: #6c757d;
  margin-top: 2px;
  font-family: 'Courier New', monospace;
`;

function ConnectionStatus({ status }) {
  const [connectionHistory, setConnectionHistory] = useState([]);

  useEffect(() => {
    // Track connection changes
    setConnectionHistory(prev => [
      ...prev.slice(-4), // Keep last 5 entries
      {
        connected: status.connected,
        timestamp: new Date(),
        socketId: status.socketId,
        reason: status.reason
      }
    ]);
  }, [status.connected, status.socketId]);

  const getStatusText = () => {
    if (status.connected) {
      return 'Connected';
    } else {
      return 'Disconnected';
    }
  };

  const getTooltipText = () => {
    const history = connectionHistory.slice(-3).map(entry => 
      `${entry.connected ? '✅' : '❌'} ${entry.timestamp.toLocaleTimeString()}`
    ).join('\n');

    return `WebSocket Status\n${history}\n\nSocket ID: ${status.socketId || 'N/A'}`;
  };

  return (
    <StatusContainer 
      connected={status.connected}
      title={getTooltipText()}
    >
      <StatusIndicator connected={status.connected} />
      <div>
        <StatusText>{getStatusText()}</StatusText>
        {status.socketId && (
          <SocketId>
            ID: {status.socketId.substring(0, 8)}...
          </SocketId>
        )}
      </div>
    </StatusContainer>
  );
}

export default ConnectionStatus;
