import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import Board from './components/Board';
import BoardSelector from './components/BoardSelector';
import ConnectionStatus from './components/ConnectionStatus';
import { useSocket } from './hooks/useSocket';
import { useBoard } from './hooks/useBoard';

const AppContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  padding: 20px;
`;

const Header = styled.header`
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(10px);
  border-radius: 12px;
  padding: 20px;
  margin-bottom: 20px;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-wrap: wrap;
  gap: 15px;
`;

const Title = styled.h1`
  color: #2c3e50;
  font-size: 28px;
  font-weight: 700;
  margin: 0;
  display: flex;
  align-items: center;
  gap: 10px;

  &::before {
    content: 'ðŸ“‹';
    font-size: 32px;
  }
`;

const Subtitle = styled.p`
  color: #7f8c8d;
  margin: 5px 0 0 0;
  font-size: 14px;
`;

const HeaderRight = styled.div`
  display: flex;
  align-items: center;
  gap: 15px;
`;

const ErrorBanner = styled.div`
  background: #e74c3c;
  color: white;
  padding: 15px;
  border-radius: 8px;
  margin-bottom: 20px;
  display: flex;
  align-items: center;
  gap: 10px;

  &::before {
    content: 'âš ï¸';
    font-size: 18px;
  }
`;

const LoadingContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 400px;
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(10px);
  border-radius: 12px;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
`;

const LoadingText = styled.div`
  text-align: center;
  color: #2c3e50;
  font-size: 18px;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 15px;
`;

function App() {
  const [currentBoardId, setCurrentBoardId] = useState(null);
  const [socketStatus, setSocketStatus] = useState({ connected: false });

  // Initialize socket connection
  const socket = useSocket(currentBoardId);

  // Board management
  const { board, lists, loading, error, createBoard, refreshBoard, setLists } = useBoard(currentBoardId);

  // Socket event handlers
  useEffect(() => {
    const handleConnectionStatus = (status) => {
      setSocketStatus(status);
    };

    const handleCardCreated = (data) => {
      console.log('Real-time card created:', data);
      refreshBoard(); // Refresh to get updated data
    };

    const handleCardUpdated = (data) => {
      console.log('Real-time card updated:', data);
      refreshBoard(); // Refresh to get updated data
    };

    const handleCardDeleted = (data) => {
      console.log('Real-time card deleted:', data);
      refreshBoard(); // Refresh to get updated data
    };

    const handleTrelloWebhook = (data) => {
      console.log('Trello webhook received:', data);
      // Handle webhook events for even more real-time updates
      refreshBoard();
    };

    // Set up event listeners
    socket.on('connection-status', handleConnectionStatus);
    socket.on('card-created', handleCardCreated);
    socket.on('card-updated', handleCardUpdated);
    socket.on('card-deleted', handleCardDeleted);
    socket.on('trello-webhook', handleTrelloWebhook);

    // Cleanup
    return () => {
      socket.off('connection-status', handleConnectionStatus);
      socket.off('card-created', handleCardCreated);
      socket.off('card-updated', handleCardUpdated);
      socket.off('card-deleted', handleCardDeleted);
      socket.off('trello-webhook', handleTrelloWebhook);
    };
  }, [socket, refreshBoard]);

  const handleBoardSelect = (boardId) => {
    setCurrentBoardId(boardId);
  };

  const handleBoardCreate = async (boardData) => {
    try {
      const newBoard = await createBoard(boardData);
      setCurrentBoardId(newBoard.id);
      return newBoard;
    } catch (error) {
      console.error('Failed to create board:', error);
      throw error;
    }
  };

  return (
    <AppContainer>
      <Header>
        <div>
          <Title>Trello Real-time Board</Title>
          <Subtitle>Real-time collaborative task management with WebSocket sync</Subtitle>
        </div>
        <HeaderRight>
          <ConnectionStatus status={socketStatus} />
          <BoardSelector
            currentBoardId={currentBoardId}
            onBoardSelect={handleBoardSelect}
            onBoardCreate={handleBoardCreate}
          />
        </HeaderRight>
      </Header>

      {error && (
        <ErrorBanner>
          Error: {error}
        </ErrorBanner>
      )}

      {loading && (
        <LoadingContainer>
          <LoadingText>
            <div className="spinner"></div>
            <div>Loading board...</div>
          </LoadingText>
        </LoadingContainer>
      )}

      {!loading && currentBoardId && board && (
        <Board
          board={board}
          lists={lists}
          onListsChange={setLists}
          onRefresh={refreshBoard}
        />
      )}

      {!loading && !currentBoardId && (
        <LoadingContainer>
          <LoadingText>
            <div style={{ fontSize: '48px' }}>ðŸŽ¯</div>
            <div>Select or create a board to get started</div>
            <div style={{ fontSize: '14px', color: '#7f8c8d' }}>
              Use the board selector in the top-right corner
            </div>
          </LoadingText>
        </LoadingContainer>
      )}
    </AppContainer>
  );
}

export default App;
