import { useState, useEffect, useCallback } from 'react';
import apiService from '../services/apiService';

export const useBoard = (boardId) => {
  const [board, setBoard] = useState(null);
  const [lists, setLists] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchBoard = useCallback(async () => {
    if (!boardId) return;

    setLoading(true);
    setError(null);

    try {
      const [boardResponse, listsResponse] = await Promise.all([
        apiService.getBoard(boardId),
        apiService.getBoardLists(boardId)
      ]);

      setBoard(boardResponse.data);
      setLists(listsResponse.data || []);
    } catch (err) {
      console.error('Error fetching board:', err);
      setError(err.message || 'Failed to load board');
    } finally {
      setLoading(false);
    }
  }, [boardId]);

  const createBoard = useCallback(async (boardData) => {
    setLoading(true);
    setError(null);

    try {
      const response = await apiService.createBoard(boardData);
      setBoard(response.data);
      return response.data;
    } catch (err) {
      console.error('Error creating board:', err);
      setError(err.message || 'Failed to create board');
      throw err;
    } finally {
      setLoading(false);
    }
  }, []);

  const refreshBoard = useCallback(() => {
    fetchBoard();
  }, [fetchBoard]);

  useEffect(() => {
    fetchBoard();
  }, [fetchBoard]);

  return {
    board,
    lists,
    loading,
    error,
    createBoard,
    refreshBoard,
    setBoard,
    setLists
  };
};
