import React, { useState } from 'react';
import styled from 'styled-components';
import { useDrop } from 'react-dnd';
import List from './List';
import { useTasks } from '../hooks/useTasks';

const BoardContainer = styled.div`
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(10px);
  border-radius: 12px;
  padding: 20px;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
  min-height: 600px;
`;

const BoardHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px;
  padding-bottom: 15px;
  border-bottom: 2px solid #ecf0f1;
`;

const BoardTitle = styled.h2`
  color: #2c3e50;
  font-size: 24px;
  font-weight: 600;
  margin: 0;
`;

const BoardMeta = styled.div`
  display: flex;
  align-items: center;
  gap: 15px;
  color: #7f8c8d;
  font-size: 14px;
`;

const ListsContainer = styled.div`
  display: flex;
  gap: 20px;
  overflow-x: auto;
  padding-bottom: 20px;
  min-height: 500px;

  &::-webkit-scrollbar {
    height: 8px;
  }

  &::-webkit-scrollbar-track {
    background: #f1f1f1;
    border-radius: 4px;
  }

  &::-webkit-scrollbar-thumb {
    background: #bdc3c7;
    border-radius: 4px;
  }

  &::-webkit-scrollbar-thumb:hover {
    background: #95a5a6;
  }
`;

const EmptyState = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 400px;
  color: #7f8c8d;
  text-align: center;

  .icon {
    font-size: 64px;
    margin-bottom: 15px;
  }

  .message {
    font-size: 18px;
    font-weight: 500;
    margin-bottom: 10px;
  }

  .description {
    font-size: 14px;
    opacity: 0.8;
  }
`;

const RefreshButton = styled.button`
  background: #3498db;
  color: white;
  border: none;
  border-radius: 6px;
  padding: 8px 16px;
  font-size: 14px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 8px;
  transition: all 0.2s;

  &:hover {
    background: #2980b9;
    transform: translateY(-1px);
  }

  &:active {
    transform: translateY(0);
  }
`;

function Board({ board, lists, onListsChange, onRefresh }) {
  const { updateTask } = useTasks();
  const [dragOverList, setDragOverList] = useState(null);

  const [, drop] = useDrop({
    accept: 'CARD',
    drop: async (item, monitor) => {
      if (monitor.didDrop()) return;

      const { cardId, sourceListId } = item;
      const targetListId = dragOverList;

      if (!targetListId || sourceListId === targetListId) {
        setDragOverList(null);
        return;
      }

      try {
        // Update card's list
        await updateTask(cardId, { idList: targetListId });
        console.log(`Moved card ${cardId} to list ${targetListId}`);

        // Refresh board to get updated data
        onRefresh();
      } catch (error) {
        console.error('Error moving card:', error);
      }

      setDragOverList(null);
    },
    collect: (monitor) => ({
      isOver: monitor.isOver(),
    }),
  });

  if (!board || !lists) {
    return (
      <BoardContainer>
        <EmptyState>
          <div className="icon">üìã</div>
          <div className="message">Board not found</div>
          <div className="description">The board you're looking for doesn't exist or couldn't be loaded.</div>
        </EmptyState>
      </BoardContainer>
    );
  }

  if (lists.length === 0) {
    return (
      <BoardContainer ref={drop}>
        <BoardHeader>
          <BoardTitle>{board.name}</BoardTitle>
          <BoardMeta>
            <RefreshButton onClick={onRefresh}>
              üîÑ Refresh
            </RefreshButton>
          </BoardMeta>
        </BoardHeader>

        <EmptyState>
          <div className="icon">üìù</div>
          <div className="message">No lists found</div>
          <div className="description">This board doesn't have any lists yet. Create lists in Trello to get started.</div>
        </EmptyState>
      </BoardContainer>
    );
  }

  return (
    <BoardContainer ref={drop}>
      <BoardHeader>
        <BoardTitle>{board.name}</BoardTitle>
        <BoardMeta>
          <span>üìä {lists.length} list{lists.length !== 1 ? 's' : ''}</span>
          <span>üéØ {lists.reduce((total, list) => total + (list.cards?.length || 0), 0)} card{lists.reduce((total, list) => total + (list.cards?.length || 0), 0) !== 1 ? 's' : ''}</span>
          <RefreshButton onClick={onRefresh}>
            üîÑ Refresh
          </RefreshButton>
        </BoardMeta>
      </BoardHeader>

      <ListsContainer>
        {lists.map((list) => (
          <List
            key={list.id}
            list={list}
            boardId={board.id}
            onDragOver={setDragOverList}
            isDragOver={dragOverList === list.id}
          />
        ))}
      </ListsContainer>
    </BoardContainer>
  );
}

export default Board;
