import React, { useState } from 'react';
import styled from 'styled-components';
import { useDrop } from 'react-dnd';
import Card from './Card';
import AddCardForm from './AddCardForm';

const ListContainer = styled.div`
  background: #f8f9fa;
  border-radius: 8px;
  padding: 15px;
  min-width: 300px;
  max-width: 300px;
  height: fit-content;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  transition: all 0.2s;

  ${props => props.isDragOver && `
    background: #e3f2fd;
    border: 2px dashed #2196f3;
    transform: scale(1.02);
  `}
`;

const ListHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 15px;
  padding-bottom: 10px;
  border-bottom: 2px solid #dee2e6;
`;

const ListTitle = styled.h3`
  color: #2c3e50;
  font-size: 16px;
  font-weight: 600;
  margin: 0;
  flex: 1;
  word-break: break-word;
`;

const CardCount = styled.span`
  background: #6c757d;
  color: white;
  border-radius: 12px;
  padding: 2px 8px;
  font-size: 12px;
  font-weight: 500;
  margin-left: 10px;
`;

const CardsContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 10px;
  min-height: 100px;
  max-height: 500px;
  overflow-y: auto;
  margin-bottom: 15px;

  &::-webkit-scrollbar {
    width: 6px;
  }

  &::-webkit-scrollbar-track {
    background: #f1f1f1;
    border-radius: 3px;
  }

  &::-webkit-scrollbar-thumb {
    background: #bdc3c7;
    border-radius: 3px;
  }

  &::-webkit-scrollbar-thumb:hover {
    background: #95a5a6;
  }
`;

const EmptyListMessage = styled.div`
  text-align: center;
  color: #6c757d;
  font-size: 14px;
  font-style: italic;
  padding: 20px 10px;
  border: 2px dashed #dee2e6;
  border-radius: 6px;
  background: #f8f9fa;
`;

const AddCardButton = styled.button`
  width: 100%;
  background: transparent;
  border: 2px dashed #dee2e6;
  border-radius: 6px;
  padding: 12px;
  color: #6c757d;
  font-size: 14px;
  cursor: pointer;
  transition: all 0.2s;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;

  &:hover {
    border-color: #3498db;
    color: #3498db;
    background: rgba(52, 152, 219, 0.05);
  }

  &:active {
    transform: scale(0.98);
  }
`;

function List({ list, boardId, onDragOver, isDragOver }) {
  const [showAddForm, setShowAddForm] = useState(false);

  const [{ isOver }, drop] = useDrop({
    accept: 'CARD',
    drop: () => {
      // The drop is handled by the Board component
      return { listId: list.id };
    },
    collect: (monitor) => ({
      isOver: monitor.isOver({ shallow: true }),
    }),
    hover: () => {
      onDragOver(list.id);
    },
  });

  const cards = list.cards || [];

  return (
    <ListContainer ref={drop} isDragOver={isDragOver || isOver}>
      <ListHeader>
        <ListTitle>{list.name}</ListTitle>
        <CardCount>{cards.length}</CardCount>
      </ListHeader>

      <CardsContainer>
        {cards.length === 0 ? (
          <EmptyListMessage>
            No cards yet. Add your first card below! ðŸ‘‡
          </EmptyListMessage>
        ) : (
          cards.map((card) => (
            <Card
              key={card.id}
              card={card}
              listId={list.id}
              boardId={boardId}
            />
          ))
        )}
      </CardsContainer>

      {showAddForm ? (
        <AddCardForm
          listId={list.id}
          boardId={boardId}
          onCancel={() => setShowAddForm(false)}
          onSuccess={() => setShowAddForm(false)}
        />
      ) : (
        <AddCardButton onClick={() => setShowAddForm(true)}>
          <span>âž•</span>
          Add a card
        </AddCardButton>
      )}
    </ListContainer>
  );
}

export default List;
