import React, { useState, useRef, useEffect } from 'react';
import styled from 'styled-components';
import { useBoard } from '../hooks/useBoard';

const SelectorContainer = styled.div`
  position: relative;
  display: flex;
  align-items: center;
  gap: 10px;
`;

const SelectorButton = styled.button`
  background: #3498db;
  color: white;
  border: none;
  border-radius: 6px;
  padding: 10px 16px;
  font-size: 14px;
  font-weight: 500;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 8px;
  transition: all 0.2s;
  min-width: 140px;
  justify-content: space-between;

  &:hover {
    background: #2980b9;
    transform: translateY(-1px);
  }

  &:active {
    transform: translateY(0);
  }
`;

const CreateButton = styled.button`
  background: #27ae60;
  color: white;
  border: none;
  border-radius: 6px;
  padding: 10px 16px;
  font-size: 14px;
  font-weight: 500;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 8px;
  transition: all 0.2s;

  &:hover {
    background: #229954;
    transform: translateY(-1px);
  }

  &:active {
    transform: translateY(0);
  }
`;

const Dropdown = styled.div`
  position: absolute;
  top: 100%;
  right: 0;
  background: white;
  border: 1px solid #dee2e6;
  border-radius: 8px;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.15);
  z-index: 1000;
  min-width: 300px;
  max-height: 400px;
  overflow-y: auto;
  margin-top: 8px;
`;

const DropdownSection = styled.div`
  padding: 12px;
  border-bottom: 1px solid #f8f9fa;

  &:last-child {
    border-bottom: none;
  }
`;

const SectionTitle = styled.h4`
  margin: 0 0 8px 0;
  color: #2c3e50;
  font-size: 12px;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.5px;
`;

const BoardInput = styled.input`
  width: 100%;
  border: 2px solid #e9ecef;
  border-radius: 6px;
  padding: 8px 12px;
  font-size: 14px;
  outline: none;
  margin-bottom: 8px;

  &:focus {
    border-color: #3498db;
    box-shadow: 0 0 0 3px rgba(52, 152, 219, 0.1);
  }
`;

const BoardButton = styled.button`
  width: 100%;
  background: transparent;
  border: 2px solid #e9ecef;
  border-radius: 6px;
  padding: 8px 12px;
  text-align: left;
  cursor: pointer;
  transition: all 0.2s;
  font-size: 14px;
  margin-bottom: 4px;

  &:hover {
    background: #f8f9fa;
    border-color: #3498db;
  }

  &:last-child {
    margin-bottom: 0;
  }
`;

const ActionButton = styled.button`
  background: ${props => props.primary ? '#3498db' : '#6c757d'};
  color: white;
  border: none;
  border-radius: 4px;
  padding: 6px 12px;
  font-size: 12px;
  cursor: pointer;
  transition: all 0.2s;
  margin-right: 8px;

  &:hover {
    background: ${props => props.primary ? '#2980b9' : '#5a6268'};
  }

  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
  }
`;

const ErrorMessage = styled.div`
  color: #e74c3c;
  font-size: 11px;
  margin-top: 4px;
  padding: 4px 8px;
  background: rgba(231, 76, 60, 0.1);
  border-radius: 4px;
`;

const RecentBoards = styled.div`
  display: flex;
  flex-direction: column;
  gap: 4px;
`;

function BoardSelector({ currentBoardId, onBoardSelect, onBoardCreate }) {
  const [isOpen, setIsOpen] = useState(false);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [newBoardName, setNewBoardName] = useState('');
  const [error, setError] = useState('');
  const [recentBoards] = useState([
    // Mock recent boards - in a real app, this would come from localStorage or API
    { id: '64a1b2c3d4e5f6789abcdef0', name: 'Sample Board 1' },
    { id: '64a1b2c3d4e5f6789abcdef1', name: 'Sample Board 2' },
    { id: '64a1b2c3d4e5f6789abcdef2', name: 'Sample Board 3' },
  ]);

  const { loading } = useBoard();
  const dropdownRef = useRef(null);

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsOpen(false);
        setShowCreateForm(false);
        setNewBoardName('');
        setError('');
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleBoardSelect = (boardId) => {
    onBoardSelect(boardId);
    setIsOpen(false);
  };

  const handleCreateBoard = async () => {
    if (!newBoardName.trim()) {
      setError('Board name is required');
      return;
    }

    setError('');

    try {
      await onBoardCreate({
        name: newBoardName.trim(),
        defaultLists: true
      });

      setNewBoardName('');
      setShowCreateForm(false);
      setIsOpen(false);
    } catch (err) {
      setError(err.message || 'Failed to create board');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleCreateBoard();
    } else if (e.key === 'Escape') {
      setShowCreateForm(false);
      setNewBoardName('');
      setError('');
    }
  };

  const getCurrentBoardName = () => {
    const current = recentBoards.find(board => board.id === currentBoardId);
    return current ? current.name : 'Select Board';
  };

  return (
    <SelectorContainer ref={dropdownRef}>
      <SelectorButton onClick={() => setIsOpen(!isOpen)}>
        <span>ðŸ“‹ {getCurrentBoardName()}</span>
        <span>{isOpen ? 'â–²' : 'â–¼'}</span>
      </SelectorButton>

      <CreateButton onClick={() => {
        setShowCreateForm(true);
        setIsOpen(true);
      }}>
        âž• New Board
      </CreateButton>

      {isOpen && (
        <Dropdown>
          {showCreateForm && (
            <DropdownSection>
              <SectionTitle>Create New Board</SectionTitle>
              <BoardInput
                type="text"
                value={newBoardName}
                onChange={(e) => setNewBoardName(e.target.value)}
                onKeyDown={handleKeyPress}
                placeholder="Enter board name..."
                autoFocus
                disabled={loading}
              />

              {error && <ErrorMessage>{error}</ErrorMessage>}

              <div>
                <ActionButton 
                  primary 
                  onClick={handleCreateBoard}
                  disabled={loading || !newBoardName.trim()}
                >
                  {loading ? 'Creating...' : 'Create'}
                </ActionButton>
                <ActionButton 
                  onClick={() => {
                    setShowCreateForm(false);
                    setNewBoardName('');
                    setError('');
                  }}
                  disabled={loading}
                >
                  Cancel
                </ActionButton>
              </div>
            </DropdownSection>
          )}

          <DropdownSection>
            <SectionTitle>Enter Board ID</SectionTitle>
            <BoardInput
              type="text"
              placeholder="Paste Trello board ID here..."
              onKeyDown={(e) => {
                if (e.key === 'Enter' && e.target.value.trim()) {
                  handleBoardSelect(e.target.value.trim());
                }
              }}
            />
          </DropdownSection>

          {recentBoards.length > 0 && (
            <DropdownSection>
              <SectionTitle>Sample Board IDs</SectionTitle>
              <RecentBoards>
                {recentBoards.map((board) => (
                  <BoardButton
                    key={board.id}
                    onClick={() => handleBoardSelect(board.id)}
                  >
                    <div style={{ fontWeight: 500 }}>{board.name}</div>
                    <div style={{ fontSize: '11px', color: '#6c757d', fontFamily: 'monospace' }}>
                      {board.id}
                    </div>
                  </BoardButton>
                ))}
              </RecentBoards>
            </DropdownSection>
          )}
        </Dropdown>
      )}
    </SelectorContainer>
  );
}

export default BoardSelector;
